import React, { useState } from 'react';

const SalesDashboard = ({ products, onSale, clients }) => {
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);

  const handleAddToSale = (product) => {
    if (product.stock > 0) {
      setSelectedProducts([...selectedProducts, product]);
    } else {
      alert('No hay suficiente stock de este producto');
    }
  };

  const handleCompleteSale = () => {
    if (selectedProducts.length === 0) return;

    const newSale = {
      id: Date.now(),
      date: new Date().toISOString(),
      products: selectedProducts.map(p => p.id),
      total: selectedProducts.reduce((sum, p) => sum + p.price, 0),
      clientId: selectedClient?.id || null
    };

    onSale(newSale);
    setSelectedProducts([]);
  };

  return (
    <div className="space-y-6">
      <h2 className="text-xl font-bold">Registro de Ventas</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {products.map(product => (
          <div key={product.id} className={`p-3 border rounded-lg ${product.stock > 0 ? 'hover:bg-gray-50' : 'opacity-50'}`}>
            <h3 className="font-medium">{product.name}</h3>
            <p className="text-gray-600">${product.price}</p>
            <p className="text-xs text-gray-500">{product.stock} disponibles</p>
            <button
              onClick={() => handleAddToSale(product)}
              disabled={product.stock <= 0}
              className={`mt-2 w-full py-1 rounded-lg text-sm ${product.stock > 0 ? 'bg-green-100 text-green-800' : 'bg-gray-200 text-gray-500'}`}
            >
              {product.stock > 0 ? '+ Agregar' : 'Sin stock'}
            </button>
          </div>
        ))}
      </div>

      {selectedProducts.length > 0 && (
        <div className="mt-6 p-4 bg-blue-50 rounded-lg">
          <h3 className="font-bold mb-2">Resumen de Venta</h3>
          <ul className="space-y-2">
            {selectedProducts.map((product, index) => (
              <li key={index} className="flex justify-between">
                <span>{product.name}</span>
                <span>${product.price}</span>
              </li>
            ))}
          </ul>
          <div className="mt-3 pt-2 border-t font-bold flex justify-between">
            <span>Total:</span>
            <span>${selectedProducts.reduce((sum, p) => sum + p.price, 0)}</span>
          </div>
          <button
            onClick={handleCompleteSale}
            className="mt-4 w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700"
          >
            Confirmar Venta
          </button>
        </div>
      )}
    </div>
  );
};

export default SalesDashboard;

