import React, { useState } from 'react';
import { clients as initialProducts, clients as initialClients } from './mock/clients';
import { users } from './mock/users';
import InventoryList from './components/InventoryList';
import SalesDashboard from './components/SalesDashboard';
import ClientManager from './components/ClientManager';
import LoginForm from './components/LoginForm';

const App = () => {
  const [currentProducts, setCurrentProducts] = useState(initialProducts);
  const [currentClients] = useState(initialClients);
  const [currentSales, setCurrentSales] = useState([]);
  const [activeTab, setActiveTab] = useState('inventory');
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);

  const handleLogin = (username, password) => {
    const user = users.find(u => u.username === username && u.password === password);
    if (user) {
      setIsLoggedIn(true);
      setCurrentUser(user);
    } else {
      alert('Credenciales incorrectas');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setCurrentUser(null);
  };

  const handleSale = (newSale) => {
    // Actualizar stock
    const updatedProducts = currentProducts.map(product => {
      if (newSale.products.includes(product.id)) {
        return { ...product, stock: product.stock - 1 };
      }
      return product;
    });

    setCurrentProducts(updatedProducts);
    setCurrentSales([...currentSales, newSale]);
  };

  const handleEditProduct = (productData, productId) => {
    if (productId) {
      setCurrentProducts(currentProducts.map(p => 
        p.id === productId ? { ...productData, id: productId } : p
      ));
    } else {
      const newId = Math.max(...currentProducts.map(p => p.id)) + 1;
      setCurrentProducts([...currentProducts, { ...productData, id: newId }]);
    }
  };

  const handleDeleteProduct = (productId) => {
    if (window.confirm('¿Estás seguro de eliminar este producto?')) {
      setCurrentProducts(currentProducts.filter(p => p.id !== productId));
    }
  };

  if (!isLoggedIn) {
    return <LoginForm onLogin={handleLogin} />;
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-6xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">PharmaSoft</h1>
          <div className="flex items-center space-x-4">
            <span className="text-sm text-gray-600">
              Conectado como: <span className="font-medium">{currentUser?.name}</span>
            </span>
            <button
              onClick={handleLogout}
              className="px-3 py-1 text-sm bg-gray-200 text-gray-700 rounded hover:bg-gray-300"
            >
              Cerrar sesión
            </button>
          </div>
        </div>

        <div className="flex space-x-4 mb-6">
          <button 
            onClick={() => setActiveTab('inventory')}
            className={`px-4 py-2 rounded-lg ${activeTab === 'inventory' ? 'bg-blue-600 text-white' : 'bg-white text-gray-700'}`}
          >
            Inventario
          </button>
          <button 
            onClick={() => setActiveTab('sales')}
            className={`px-4 py-2 rounded-lg ${activeTab === 'sales' ? 'bg-blue-600 text-white' : 'bg-white text-gray-700'}`}
          >
            Ventas
          </button>
          <button 
            onClick={() => setActiveTab('clients')}
            className={`px-4 py-2 rounded-lg ${activeTab === 'clients' ? 'bg-blue-600 text-white' : 'bg-white text-gray-700'}`}
          >
            Clientes
          </button>
        </div>

        {activeTab === 'inventory' && (
          <InventoryList
            products={currentProducts}
            onEdit={handleEditProduct}
            onDelete={handleDeleteProduct}
            isAdmin={currentUser?.role === 'admin'}
          />
        )}
        {activeTab === 'sales' && (
          <SalesDashboard 
            products={currentProducts} 
            onSale={handleSale} 
            clients={currentClients} 
          />
        )}
        {activeTab === 'clients' && <ClientManager clients={currentClients} />}
      </div>
    </div>
  );
};

export default App;
