import React, { useState } from 'react';
import InventoryAlert from './InventoryAlert';
import ProductForm from './ProductForm';

const InventoryList = ({ products, onEdit, onDelete, isAdmin }) => {
  const [showForm, setShowForm] = useState(false);
  const [currentProduct, setCurrentProduct] = useState(null);

  const handleEdit = (product) => {
    setCurrentProduct(product);
    setShowForm(true);
  };

  const handleAddNew = () => {
    setCurrentProduct(null);
    setShowForm(true);
  };

  const handleSubmit = (productData) => {
    onEdit(productData, currentProduct?.id);
    setShowForm(false);
  };

  return (
    <div className="space-y-6">
      {isAdmin && (
        <div className="flex justify-between items-center">
          <h2 className="text-xl font-bold">Inventario de Farmacia</h2>
          <button
            onClick={handleAddNew}
            className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors"
          >
            + Agregar Medicamento
          </button>
        </div>
      )}

      {showForm ? (
        <ProductForm
          product={currentProduct}
          onSubmit={handleSubmit}
          onCancel={() => setShowForm(false)}
        />
      ) : (
        <div className="space-y-4">
          {products.map(product => (
            <div key={product.id} className="p-4 border rounded-lg shadow-sm hover:shadow-md transition-shadow">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="font-medium">{product.name}</h3>
                  <p className="text-gray-600">{product.lab}</p>
                  <p className="text-sm text-gray-500">
                    {product.requiresPrescription ? 'ðŸ’Š Requiere receta' : 'ðŸ†“ Venta libre'} | 
                    ðŸ“… Vence: {product.expiryDate}
                  </p>
                </div>
                <div className="flex items-center space-x-2">
                  <InventoryAlert product={product} />
                  <span className="font-medium">${product.price}</span>
                </div>
              </div>

              {isAdmin && (
                <div className="flex justify-end space-x-2 mt-3">
                  <button
                    onClick={() => handleEdit(product)}
                    className="px-3 py-1 bg-yellow-500 text-white rounded hover:bg-yellow-600 text-sm transition-colors"
                  >
                    Editar
                  </button>
                  <button
                    onClick={() => onDelete(product.id)}
                    className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600 text-sm transition-colors"
                  >
                    Eliminar
                  </button>
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default InventoryList;
